import React, { useState } from 'react';
import axios from 'axios';

import {Link} from 'react-router-dom';

import './agendamento.css';



function Agendamento(){

    const [nome, setNome] = useState('');
    const [descricao, setDescricao] = useState('');
    const [show, setShow] = useState(false);

    const handleClick = (e) => {
        e.preventDefault();
        setShow(!show);
    }


    async function Submit(ev){


        ev.preventDefault();
    
        const data = {
            'nome' : nome,
            'descricao' : descricao
        }
    
    
        await axios.post('https://bycoders-agendei.herokuapp.com/evento/salvar', data)
                .then((response) => {
                    history.push('/');
                });
        
        console.log(data);
    
    }
    return(
        <div className="agendamento">
            <div className="agendamento-box">
                <div className="agendamento-left">
                    <h1> Nome </h1>
                    <br/>
                    <Link to="/">
                        <h4 className='logout'>
                            Logout
                        </h4>
                    </Link>
                    <button className="agendar">
                        Agendar
                    </button>
                </div>
                <div className="agendamento-right">
                    <div className="agendamento-input1">
                        <h1> Nome: </h1>
                        <br/>
                        <input 
                            required
                            type="text" 
                            value={nome}
                            onChange={e => setNome(e.target.value)}
                        />
                    </div>
                    <div className="agendamento-input2">
                        <h1> Data: </h1>
                        <br/>
                        <input 
                            required
                            type="date" 
                        />
                    </div>
                    <div className="agendamento-input3">
                        <h1> Descrição: </h1>
                        <br/>
                        <input 
                            required
                            type="text" 
                            value={descricao}
                            onChange={e => setDescricao(e.target.value)}
                        />
                    </div>

                    {/* <Link to='/agenda'> */}
                        <button className="concluir" onClick={Submit}>
                            Concluir
                        </button>
                    {/* </Link> */}

                </div>
            </div>
        </div>    
    )
}

export default Agendamento;
