import axios from 'axios';
import React, { Component } from 'react'
import { BiTrash } from 'react-icons/bi';
import {Link} from 'react-router-dom';
import api from '../../api';

import './agenda.css';

class Agenda extends Component{


    state = {
        atvs: [],
    };

    async componentDidMount(){     
        const response = await api.get('');

        this.setState({atvs: response.data})
    }

    render(){

        const {atvs} = this.state;

        function onClickDelete(id){
            if(window.confirm('Deseja realmente deletar essa atividade?')){

                axios.delete('https://bycoders-agendei.herokuapp.com/eventos/delete/' + id)

                this.setState({
                    atvs: this.state.atvs.filter(atv => atv.id !== id)
                })
            }
        }


        return(
            <div className="agenda">
                <div className="agenda-box">
                    <div className="agenda-left">
                        <h1> Nome </h1>
                        <br/>
                        <Link to="/">
                            <h4 className='logout'>
                                Logout
                            </h4>
                        </Link>

                        <Link to='/agendamento'>
                            <button className="agendar">
                                Agendar
                            </button>
                        </Link>
                    </div>
                    <div className="agenda-right">
                        {atvs.map (atv => (
                            <div className="agenda-boxone" key={atv.id}>
                                <h2>
                                    Titulo: 
                                    {atv.dia}
                                </h2>

                                <p>
                                    {atv.descricao}
                                </p>

                                <button type="button" className="lixeira" onClick={() => onClickDelete(atv.id)}>
                                    <BiTrash/>
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>    
        )
    }
}

export default Agenda;
